mapper.select:
select
id, user_id, song_id, song_list_id, content, create_time, type, up
from music.comment


        and id = #{id}


        and user_id = #{userId}


        and song_id = #{songId}


        and song_list_id = #{songListId}


        and content = #{content}


        and create_time = #{createTime}


        and type = #{type}


        and up = #{up}
